import React, { useState } from 'react'
import axios from 'axios'; // API 
import Joi from 'joi';// for validation


export default function Register() {
  let [errorList , setErrorList]=useState([]);
  function submitform(e){
    // alert('test');
    e.preventDefault();
    let validateform = validator();
    if(validateform.error){
      setErrorList()
    }
    console.log(validateform);
  }
  let [user , setUser]=useState({
    first_name:'',
    last_name:'',
    email:'',
  })

function validator(){
  const schema = Joi.object({
    first_name: Joi.string().max(3).required(),
    last_name: Joi.string().required().pattern(new RegExp('^[a-z][0-9]{3}$')) // Regax code on react Jo
});
return schema.validate(user);
}


function getFormvalue(e){
  let  myUser = {...user};
  myUser[e.target.name]=e.target.value; // to get data by name=""
setUser(myUser);

  console.log(myUser); // to set copy for parent let 
}

  function change(e){
console.log(e.target.value);
  }
  return (
<form onSubmit={submitform}>
  <div className="form-group">
    <label htmlFor="first_name">First Name</label>
    <input onChange={getFormvalue} type="text" className="form-control" id="first_name" aria-describedby="emailHelp"name='first_name'/>
  </div>
  <div className="form-group">
    <label htmlFor="last_name">last Name</label>
    <input onChange={getFormvalue} type="text" className="form-control" id="last_name" aria-describedby="emailHelp" name='last_name'/>
  </div>
  <div className="form-group">
    <label htmlFor="email">Eamil</label>
    <input onChange={getFormvalue} type="email" className="form-control" id="email" aria-describedby="emailHelp"name='email' />
  </div>
  <button type="submit" className="btn btn-primary float-l eft">Submit</button>
</form>

  )
}
